package decimal

import (
	"gopkg.in/inf.v0"
)

// Precision defines the minimum precision all inexact decimal
// calculations should attempt to achieve.
const Precision = 16

// Read-only constants used for compuation.
var (
	decimalHalf      = inf.NewDec(5, 1)
	decimalOneEighth = inf.NewDec(125, 3)
	decimalOne       = inf.NewDec(1, 0)
	decimalTwo       = inf.NewDec(2, 0)
	decimalThree     = inf.NewDec(3, 0)
	decimalEight     = inf.NewDec(8, 0)
	decimalLog2      = new(inf.Dec)
	decimalLog10     = new(inf.Dec)
	decimalCbrtC1    = new(inf.Dec)
	decimalCbrtC2    = new(inf.Dec)
	decimalCbrtC3    = new(inf.Dec)
	// Unused constants for now.
	// decimalE          = new(inf.Dec)
	// decimalPi         = new(inf.Dec)
)

func init() {
	if _, ok := decimalLog2.SetString(strLog2); !ok {
		panic("error setting log(2)")
	}
	if _, ok := decimalLog10.SetString(strLog10); !ok {
		panic("error setting log(10)")
	}
	if _, ok := decimalCbrtC1.SetString(strCbrtC1); !ok {
		panic("error setting decimalCbrtC1")
	}
	if _, ok := decimalCbrtC2.SetString(strCbrtC2); !ok {
		panic("error setting decimalCbrtC2")
	}
	if _, ok := decimalCbrtC3.SetString(strCbrtC3); !ok {
		panic("error setting decimalCbrtC3")
	}
	// if _, ok := decimalE.SetString(strE); !ok {
	// 	panic("error setting e")
	// }
	// if _, ok := decimalPi.SetString(strPi); !ok {
	// 	panic("error setting pi")
	// }
}

const strE = "2."

// const strPi = "3."

const strLog2 = "0."

const strLog10 = "2."

// Cbrt uses a quadratic polynomial that approximates the cube root
// of x when 0.125 <= x <= 1. This approximation is the starting point
// of the convergence loop. Coefficients are from:
// https://people.freebsd.org/~lstewart/references/apple_tr_kt32_cuberoot.pdf

const strCbrtC1 = "-0.46946116"

const strCbrtC2 = "1.072302"

const strCbrtC3 = "0.3812513"
